import numpy as np

def _to_arr(x):
    return np.asarray(x, float).ravel()

def _valid(a, w):
    return w > 0 and a.size >= w

def weighted(x, w=20):
    a = _to_arr(x)
    if not _valid(a, w):
        return np.empty(0, float)
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return np.convolve(a, wt, mode="valid")

def process_signal(sig, w=20, alg="weighted"):
    alg = (alg or "weighted").lower()
    if alg in ("basic", "adaptive"):
        a = _to_arr(sig)
        if not _valid(a, w):
            return np.empty(0, float)
        return np.convolve(a, np.ones(w) / w, mode="valid")
    if alg in ("weighted", "exp", "exponential"):
        return weighted(sig, w)
    return weighted(sig, w)
