import numpy as np
from functools import lru_cache

def _arr(x):
    return np.asarray(x, dtype=float).ravel()

def _ma(x, w):
    a = _arr(x)
    return np.convolve(a, np.ones(w) / w, "valid") if w > 0 and a.size >= w else np.empty(0)

def _ema(x, w):
    a = _arr(x)
    if w <= 0 or a.size < w:
        return np.empty(0)
    alpha = 2.0 / (w + 1)
    out = np.empty_like(a)
    out[0] = a[0]
    for i in range(1, len(a)):
        out[i] = alpha * a[i] + (1 - alpha) * out[i - 1]
    return out[w - 1 :]

@lru_cache(maxsize=None)
def _gw(w):
    sigma = 0.3 * w
    c = (w - 1) / 2.0
    idx = np.arange(w)
    wts = np.exp(-0.5 * ((idx - c) / sigma) ** 2)
    return wts / wts.sum()

def _gauss(x, w):
    a = _arr(x)
    return np.convolve(a, _gw(w), "valid") if w > 0 and a.size >= w else np.empty(0)

def _median(x, w):
    a = _arr(x)
    if w <= 0 or a.size < w:
        return np.empty(0)
    s = a.strides[0]
    shape = (a.size - w + 1, w)
    win = np.lib.stride_tricks.as_strided(a, shape=shape, strides=(s, s))
    return np.median(win, axis=1)

def _resvar(orig, filt):
    if filt.size == 0:
        return np.inf
    n = min(len(orig), len(filt))
    return np.var(orig[:n] - filt[:n])

def _best(x, w):
    a = _arr(x)
    cand = {
        "ma": _ma(a, w),
        "ema": _ema(a, w),
        "gauss": _gauss(a, w),
        "median": _median(a, w),
    }
    cand = {k: v for k, v in cand.items() if v.size}
    if not cand:
        return np.empty(0)
    return min(cand.values(), key=lambda f: _resvar(a, f))

