import numpy as np

def _to_array(x):
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()


def _validate_window(w: int):
    """Ensure window size is a positive integer."""
    if not isinstance(w, int) or w <= 0:
        raise ValueError("window_size must be a positive integer")


def _basic(x: np.ndarray, w: int) -> np.ndarray:
    """Simple moving‑average (valid convolution)."""
    _validate_window(w)
    if x.size < w:
        return np.array([], dtype=float)
    return np.convolve(x, np.ones(w) / w, mode="valid")


def _weighted(x: np.ndarray, w: int) -> np.ndarray:
    """Weighted moving‑average with exponential‑decay kernel."""
    _validate_window(w)
    if x.size < w:
        return np.array([], dtype=float)
    weights = np.exp(np.linspace(-2, 0, w))
    weights /= weights.sum()
    return np.convolve(x, weights, mode="valid")


def _ema(x: np.ndarray, w: int) -> np.ndarray:
    """Exponential moving average aligned to the length of _basic."""
    _validate_window(w)
    if x.size < w:
        return np.array([], dtype=float)
    alpha = 2.0 / (w + 1)
    e = np.empty_like(x)
    e[0] = x[0]
    for i in range(1, x.size):
        e[i] = alpha * x[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]


def _median(x: np.ndarray, w: int) -> np.ndarray:
    """Median filter over a sliding window (valid length)."""
    _validate_window(w)
    if x.size < w:
        return np.array([], dtype=float)
    stride = x.strides[0]
    shape = (x.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(x, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _linear_detrend(x: np.ndarray) -> np.ndarray:
    """Remove a linear trend using least‑squares regression."""
    n = x.size
    if n == 0:
        return x
    t = np.arange(n, dtype=float)
    A = np.vstack([t, np.ones_like(t)]).T
    slope, intercept = np.linalg.lstsq(A, x, rcond=None)[0]
    return x - (slope * t + intercept)


def _enhanced(x: np.ndarray, w: int) -> np.ndarray:
    """
    Detrend with linear regression then apply a weighted moving‑average.
    This preserves the underlying shape while suppressing low‑frequency drift.
    """
    if x.size < w:
        return np.array([], dtype=float)
    detrended = _linear_detrend(x)
    return _weighted(detrended, w)


def _combined(x: np.ndarray, w: int) -> np.ndarray:
    """Apply the enhanced filter followed by a median filter for outlier robustness."""
    enhanced = _enhanced(x, w)
    if enhanced.size == 0:
        return np.array([], dtype=float)
    return _median(enhanced, w)


def _residual_var(orig: np.ndarray, filt: np.ndarray) -> float:
    """Mean variance of the difference between original and filtered signals."""
    if filt.size == 0:
        return float("inf")
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n]))


def _best_filter(x: np.ndarray, w: int) -> np.ndarray:
    """Select the filter that yields the smallest residual variance."""
    candidates = {
        "basic": _basic(x, w),
        "weighted": _weighted(x, w),
        "ema": _ema(x, w),
        "enhanced": _enhanced(x, w),
        "combined": _combined(x, w),
    }
    # Prefer the candidate with minimal residual variance; fallback to basic if all empty
    best = min(candidates.values(), key=lambda f: _residual_var(x, f))
    return best if best.size else np.array([], dtype=float)


def process_signal(input_signal, window_size=20, algorithm_type="combined"):
    """
    Dispatch to the requested filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "basic" or "ma": simple moving average
        - "weighted": exponential‑decay weighted average
        - "ema": exponential moving average
        - "enhanced": detrended weighted filter
        - "combined": enhanced followed by median
        - any other value: automatic selection of the best filter
    """
    x = _to_array(input_signal)
    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        return _basic(x, window_size)
    if alg == "weighted":
        return _weighted(x, window_size)
    if alg == "ema":
        return _ema(x, window_size)
    if alg == "enhanced":
        return _enhanced(x, window_size)
    if alg == "combined":
        return _combined(x, window_size)
    # Fallback to automatic best‑filter selection
    return _best_filter(x, window_size)
