import numpy as np
from functools import lru_cache

def _to_array(x):
    """1‑D float array."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr, w):
    return isinstance(w, int) and w > 0 and arr.size >= w

@lru_cache(maxsize=64)
def _exp_weights(w):
    w = int(w)
    wts = np.exp(np.linspace(-2, 0, w))
    return wts / wts.sum()

def adaptive_filter(x, window_size=20):
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, mode="valid")

def weighted_filter(x, window_size=20):
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, _exp_weights(w), mode="valid")

def ema_filter(x, window_size=20):
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]

def _residual_variance(orig, filt):
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n])) if n else float("inf")

def _combined_filter(x, window_size=20):
    """Inverse‑variance weighted blend of adaptive, weighted, and EMA filters."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)

    cand = {
        "adaptive": adaptive_filter(a, w),
        "weighted": weighted_filter(a, w),
        "ema": ema_filter(a, w),
    }
    cand = {k: v for k, v in cand.items() if v.size}
    if not cand:
        return np.empty(0, dtype=float)

    vari = {k: _residual_variance(a, v) for k, v in cand.items()}
    # perfect match shortcut
    for k, v in vari.items():
        if v == 0.0:
            return cand[k][: len(a) - w + 1]

    keys = sorted(vari)
    inv = np.array([1.0 / vari[k] for k in keys])
    wts = inv / inv.sum()
    min_len = min(len(cand[k]) for k in keys)
    blended = sum(wts[i] * cand[k][:min_len] for i, k in enumerate(keys))
    return blended

def _auto_select(x, window_size=20):
    a = _to_array(x)
    w = int(window_size)
    cand = {
        "adaptive": adaptive_filter(a, w),
        "weighted": weighted_filter(a, w),
        "ema": ema_filter(a, w),
        "combined": _combined_filter(a, w),
    }
    cand = {k: v for k, v in cand.items() if v.size}
    if not cand:
        return np.empty(0, dtype=float)
    best = min(cand, key=lambda k: _residual_variance(a, cand[k]))
    return cand[best]

