import numpy as np

def _to_numpy(x, w):
    """Convert input to 1‑D float array; return empty array if length < w."""
    arr = np.asarray(x, dtype=float).ravel()
    return arr if w > 0 and arr.size >= w else np.empty(0, dtype=float)


def _simple_ma(x, w):
    """Simple moving average (valid convolution)."""
    a = _to_numpy(x, w)
    if a.size == 0:
        return a
    return np.convolve(a, np.ones(w) / w, mode="valid")


def _weighted_ma(x, w):
    """Exponentially weighted moving average using a fixed kernel."""
    a = _to_numpy(x, w)
    if a.size == 0:
        return a
    weights = np.exp(np.linspace(-2, 0, w))
    weights /= weights.sum()
    return np.convolve(a, weights, mode="valid")


def _ema(x, w):
    """Classic exponential moving average (recursive)."""
    a = _to_numpy(x, w)
    if a.size == 0:
        return a
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, len(a)):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _enhanced(x, w):
    """Detrend with simple MA then apply weighted smoothing."""
    a = _to_numpy(x, w)
    if a.size == 0:
        return a
    trend = _simple_ma(a, w)
    # Pad trend to original length using edge value replication
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = a - trend_full
    return _weighted_ma(detrended, w)


def _residual_variance(orig, filt):
    """Mean variance of the residual over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))


def _best_filter(x, w):
    """Select the filter with the smallest residual variance."""
    candidates = {
        "basic": _simple_ma(x, w),
        "weighted": _weighted_ma(x, w),
        "ema": _ema(x, w),
        "enhanced": _enhanced(x, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    orig = _to_numpy(x, w)
    best_key = min(candidates, key=lambda k: _residual_variance(orig, candidates[k]))
    return candidates[best_key]


def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """
    Dispatch to the requested filtering algorithm.
    Supported types (case‑insensitive):
        - "basic": simple moving average
        - "weighted": exponentially weighted moving average
        - "ema": classic exponential moving average
        - "enhanced": detrended weighted filter
    Any other value triggers automatic selection of the best filter.
    """
    alg = (algorithm_type or "").lower()
    if alg == "basic":
        return _simple_ma(input_signal, window_size)
    if alg == "weighted":
        return _weighted_ma(input_signal, window_size)
    if alg == "ema":
        return _ema(input_signal, window_size)
    if alg == "enhanced":
        return _enhanced(input_signal, window_size)
    return _best_filter(input_signal, window_size)
