def _ema(x, w):
    a = _to_numpy(x)
    if w <= 0 or a.size < w:
        return np.array([], dtype=float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, len(a)):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _gaussian(x, w):
    a = _to_numpy(x)
    if w <= 0 or a.size < w:
        return np.array([], dtype=float)
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w)
    wts = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    wts /= wts.sum()
    return np.convolve(a, wts, mode="valid")


