import numpy as np
from functools import lru_cache
from typing import List

def _to_array(x):
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr, w):
    """Check that window size is positive and fits the array."""
    return isinstance(w, int) and w > 0 and arr.size >= w

@lru_cache(maxsize=64)
def _exp_weights(w):
    """Cached right‑biased exponential weights that sum to 1."""
    w = int(w)
    e = np.exp(np.linspace(-2, 0, w))
    return e / e.sum()

def adaptive_filter(x, window_size=20):
    """Simple moving‑average filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, mode="valid")

def weighted_filter(x, window_size=20):
    """Exponentially weighted moving‑average filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, _exp_weights(w), mode="valid")

def ema_filter(x, window_size=20):
    """Recursive exponential moving‑average (EMA) filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]

def hybrid_filter(x, window_size=20):
    """Average of adaptive and weighted filters (same length)."""
    s = adaptive_filter(x, window_size)
    w = weighted_filter(x, window_size)
    if s.size == 0:
        return w
    if w.size == 0:
        return s
    return (s + w) / 2.0

def _residual_variance(orig, filt):
    """Variance of the residual (orig – filt) over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))

def _auto_select(x, window_size=20):
    """Pick the filter with the lowest residual variance."""
    a = _to_array(x)
    w = int(window_size)
    candidates = {
        "adaptive": adaptive_filter(a, w),
        "weighted": weighted_filter(a, w),
        "ema": ema_filter(a, w),
        "hybrid": hybrid_filter(a, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
    return candidates[best_key]

def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "adaptive" / "basic"
        - "weighted"
        - "ema"
        - "enhanced" / "hybrid"
        - "auto"   – automatic selection based on residual variance
    """
    alg = (algorithm_type or "").lower()
    if alg in ("adaptive", "basic"):
        return adaptive_filter(input_signal, window_size)
    if alg == "weighted":
        return weighted_filter(input_signal, window_size)
    if alg == "ema":
        return ema_filter(input_signal, window_size)
    if alg in ("enhanced", "hybrid"):
        return hybrid_filter(input_signal, window_size)
    if alg == "auto":
        return _auto_select(input_signal, window_size)
    # Fallback to enhanced for unknown types
    return hybrid_filter(input_signal, window_size)

def _best_fit_bin(item_size: int, remaining: List[int]) -> int:
    """Return index of bin with the smallest remaining space that fits the item,
    or -1 if no bin can accommodate it."""
    best_idx = -1
    min_space = None
    for i, space in enumerate(remaining):
        if item_size <= space:
            leftover = space - item_size
            if min_space is None or leftover < min_space:
                min_space = leftover
                best_idx = i
    return best_idx

def construct_packing(items: List[int], capacity: int) -> List[List[int]]:
    """
    First‑Fit Decreasing (with best‑fit tie‑breaker) bin‑packing.

    Parameters
    ----------
    items : List[int]
        Non‑negative sizes of items to pack.
    capacity : int
        Positive capacity of each bin.

    Returns
    -------
    List[List[int]]
        A list of bins, each containing the original indices of items placed in that bin.
    """
    if capacity <= 0:
        raise ValueError("capacity must be a positive integer")
    if not items:
        return []

    # Pair each item with its original index and sort descending by size
    indexed = [(idx, size) for idx, size in enumerate(items)]
    indexed.sort(key=lambda x: x[1], reverse=True)

    bins: List[List[int]] = []
    remaining: List[int] = []  # remaining capacity for each bin

    for idx, size in indexed:
        if size > capacity:
            # Item too large: allocate its own bin (cannot be combined)
            bins.append([idx])
            remaining.append(0)
            continue

        bin_idx = _best_fit_bin(size, remaining)
        if bin_idx >= 0:
            bins[bin_idx].append(idx)
            remaining[bin_idx] -= size
        else:
            # Open a new bin
            bins.append([idx])
            remaining.append(capacity - size)

    # Optional: sort indices within each bin for readability
    for b in bins:
        b.sort()
    return bins
