    return w > 0 and a.size >= w

def _conv(a, w, weights=None):
    if not _valid(a, w):
        return np.empty(0, float)
    if weights is None:
        weights = np.ones(w) / w
    else:
        weights = np.asarray(weights, float)
        if weights.size != w:
            raise ValueError("Weight length must equal window size")
        weights = weights / weights.sum()
    return np.convolve(a, weights, mode="valid")

def _ma(a, w):
    return _conv(a, w)

def _gaussian(a, w):
    if not _valid(a, w):
        return np.empty(0, float)
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w, dtype=float)
    kernel = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    return _conv(a, w, kernel)

def _ema(a, w):
    if not _valid(a, w):
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    e = np.empty_like(a)
    e[0] = a[0]
    for i in range(1, a.size):
        e[i] = alpha * a[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]

