    """Recursive exponential moving‑average (EMA) filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]

def hybrid_filter(x, window_size=20):
    """Average of adaptive and weighted filters (same length)."""
    a = adaptive_filter(x, window_size)
    b = weighted_filter(x, window_size)
    if a.size == 0:
        return b
    if b.size == 0:
        return a
    return (a + b) / 2.0

def _residual_variance(orig, filt):
    """Variance of the residual over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))

def _auto_select(x, window_size=20):
    """Pick the filter with the lowest residual variance."""
    a = _to_array(x)
    w = int(window_size)
    candidates = {
        "adaptive": adaptive_filter(a, w),
        "weighted": weighted_filter(a, w),
        "ema": ema_filter(a, w),
        "hybrid": hybrid_filter(a, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
    return candidates[best_key]

def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "adaptive" / "basic"
        - "weighted"
        - "ema"
        - "enhanced" / "hybrid"
        - "auto"   – automatic selection based on residual variance
    Unknown values fall back to the hybrid filter for robustness.
    """
    alg = (algorithm_type or "").lower()
    if alg in ("adaptive", "basic"):
        return adaptive_filter(input_signal, window_size)
    if alg == "weighted":
        return weighted_filter(input_signal, window_size)
    if alg == "ema":
        return ema_filter(input_signal, window_size)
    if alg in ("enhanced", "hybrid"):
        return hybrid_filter(input_signal, window_size)
    if alg == "auto":
        return _auto_select(input_signal, window_size)
    # Fallback to hybrid for any unrecognised option
    return hybrid_filter(input_signal, window_size)

def construct_packing(items: List[int], capacity: int) -> List[List[int]]:
    """
    Best‑Fit Decreasing bin‑packing.

    Parameters
    ----------
    items : List[int]
        Sizes of the items to pack. Must be non‑negative numbers.
    capacity : int
        Maximum total size each bin can hold. Must be positive.

    Returns
    -------
    List[List[int]]
        A list of bins; each bin is a list of original item indices
        (0‑based) that fit within the given capacity.
    """
    if capacity <= 0:
        raise ValueError("capacity must be a positive integer")
    if not items:
        return []

    # Pair each item with its original index and validate
    indexed = []
    for idx, size in enumerate(items):
        if not isinstance(size, (int, float)):
            raise TypeError("item sizes must be numeric")
        if size < 0:
            raise ValueError("item sizes must be non‑negative")
        indexed.append((idx, float(size)))

    # Sort by size descending (largest first)
    indexed.sort(key=lambda pair: pair[1], reverse=True)

    bins: List[List[int]] = []
    remaining: List[float] = []  # remaining capacity per bin

    for idx, size in indexed:
        # Items larger than a single bin get their own bin (cannot be split)
        if size > capacity:
            bins.append([idx])
            remaining.append(0.0)
            continue

        # Find the bin that leaves the smallest leftover space after placement
        best_bin = -1
        best_rem = float("inf")
        for b_idx, cap_left in enumerate(remaining):
            if size <= cap_left and cap_left - size < best_rem:
                best_bin = b_idx
                best_rem = cap_left - size

        if best_bin >= 0:
            bins[best_bin].append(idx)
            remaining[best_bin] -= size
        else:
            # No existing bin fits; open a new one
            bins.append([idx])
            remaining.append(capacity - size)

    # Optional: sort indices inside each bin for deterministic output
    for b in bins:
        b.sort()
    return bins
