def construct_packing(items, capacity):
    """
    First‑Fit Decreasing bin‑packing.

    Parameters
    ----------
    items : sequence of non‑negative numbers
        Sizes of items to pack.
    capacity : positive number
        Maximum total size per bin.

    Returns
    -------
    list of lists
        Each inner list contains the original indices of items placed in that bin.
    """
    if capacity <= 0:
        raise ValueError("capacity must be positive")
    if not items:
        return []

    # Sort items by size descending, keep original indices
    sorted_items = sorted(enumerate(items), key=lambda t: t[1], reverse=True)

    bins = []          # list of lists of indices
    remaining = []     # remaining capacity per bin

    for idx, size in sorted_items:
        if size > capacity:
            # Oversized items get their own bin (cannot be combined)
            bins.append([idx])
            remaining.append(0)
            continue

        placed = False
        for b_idx, cap in enumerate(remaining):
            if size <= cap:
                bins[b_idx].append(idx)
                remaining[b_idx] -= size
                placed = True
                break

        if not placed:
            bins.append([idx])
            remaining.append(capacity - size)

    # Sort indices inside each bin for deterministic output
    for b in bins:
        b.sort()
    return bins
