import numpy as np

def _to_numpy(x):
    """Convert input to a 1‑D float ndarray."""
    return np.asarray(x, dtype=float).ravel()


def _validate_window(w):
    """Ensure the window size is a positive integer."""
    if not isinstance(w, int) or w <= 0:
        raise ValueError("window_size must be a positive integer")


def _ma(x, w):
    """Simple moving‑average filter (valid convolution)."""
    _validate_window(w)
    arr = _to_numpy(x)
    if arr.size < w:
        return np.array([], dtype=float)
    return np.convolve(arr, np.ones(w) / w, mode="valid")


def _gaussian(x, w):
    """Weighted moving‑average with a Gaussian kernel."""
    _validate_window(w)
    arr = _to_numpy(x)
    if arr.size < w:
        return np.array([], dtype=float)
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w)
    weights = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    weights /= weights.sum()
    return np.convolve(arr, weights, mode="valid")


def _ema(x, w):
    """Exponential moving average aligned to MA length."""
    _validate_window(w)
    arr = _to_numpy(x)
    if arr.size < w:
        return np.array([], dtype=float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(arr)
    ema[0] = arr[0]
    for i in range(1, arr.size):
        ema[i] = alpha * arr[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _median(x, w):
    """Median filter over a sliding window."""
    _validate_window(w)
    arr = _to_numpy(x)
    if arr.size < w:
        return np.array([], dtype=float)
    stride = arr.strides[0]
    shape = (arr.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(arr, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _detrended_weighted(x, w):
    """
    Detrend using a simple moving average, then smooth the detrended
    series with a Gaussian kernel.
    """
    trend = _ma(x, w)
    if trend.size == 0:
        return np.array([], dtype=float)
    # Pad the trend so it aligns with the original signal length
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = _to_numpy(x) - trend_full
    return _gaussian(detrended, w)


def _combined_filter(x, w):
    """
    Detrend → Gaussian smoothing → Median smoothing.
    Provides a more aggressive noise‑reduction pipeline.
    """
    weighted = _detrended_weighted(x, w)
    if weighted.size == 0:
        return np.array([], dtype=float)
    # Apply median on the already‑smoothed signal
    return _median(weighted, w)


def _residual_variance(orig, filt):
    """Mean variance of the difference between original and filtered signals."""
    if filt.size == 0:
        return float("inf")
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n]))


def _best_filter(x, w):
    """
    Select the filter with the lowest residual variance.
    Deterministic tie‑break order: combined, detrended, gaussian, ema, median, ma.
    """
    orig = _to_numpy(x)
    candidates = {
        "ma": _ma(orig, w),
        "gaussian": _gaussian(orig, w),
        "ema": _ema(orig, w),
        "median": _median(orig, w),
        "detrended": _detrended_weighted(orig, w),
        "combined": _combined_filter(orig, w),
    }

    # Filter out empty results
    viable = {k: v for k, v in candidates.items() if v.size}
    if not viable:
        return np.array([], dtype=float)

    variances = {k: _residual_variance(orig, v) for k, v in viable.items()}
    best_val = min(variances.values())
    tie_order = ("combined", "detrended", "gaussian", "ema", "median", "ma")
    for key in tie_order:
        if key in variances and variances[key] == best_val:
            return viable[key]
    # Fallback (should never reach here)
    return next(iter(viable.values()))


def process_signal(sig, w=20, algorithm_type="combined"):
    """
    Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "basic" or "ma": simple moving average
        - "weighted": Gaussian weighted average
        - "ema": exponential moving average
        - "median": median filter
        - "detrended": detrended‑weighted filter
        - "combined": detrended → gaussian → median pipeline
        - "enhanced", "auto", "combined": automatic selection of the best filter
    Any other value falls back to the automatic selection.
    """
    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        return _ma(sig, w)
    if alg == "weighted":
        return _gaussian(sig, w)
    if alg == "ema":
        return _ema(sig, w)
    if alg == "median":
        return _median(sig, w)
    if alg == "detrended":
        return _detrended_weighted(sig, w)
    if alg == "combined":
        return _combined_filter(sig, w)
    # enhanced / auto / unknown → best filter
    return _best_filter(sig, w)
