import numpy as np
from functools import lru_cache

def _to_int(value, name):
    """Convert *value* to a positive int, raising a clear ValueError on failure."""
    try:
        iv = int(value)
    except Exception as exc:
        raise ValueError(f"{name} must be an integer, got {value!r}") from exc
    if iv <= 0:
        raise ValueError(f"{name} must be positive, got {iv}")
    return iv


def _prepare(x, window_size):
    """Return a 1‑D float array and a validated window size.

    If the array is shorter than the window, an empty array is returned
    (instead of raising) so downstream filters can handle the edge case
    gracefully.
    """
    w = _to_int(window_size, "window_size")
    arr = np.asarray(x, dtype=float).ravel()
    if arr.size < w:
        return np.empty(0, dtype=float), w
    return arr, w


@lru_cache(maxsize=64)
def _exp_weights(w):
    """Cached right‑biased exponential weights that sum to 1."""
    wts = np.exp(np.linspace(-2, 0, w))
    return wts / wts.sum()


def adaptive_filter(x, window_size=20):
    """
    Simple moving‑average filter (causal).

    Returns an empty array if the input is shorter than ``window_size``.
    """
    a, w = _prepare(x, window_size)
    if a.size == 0:
        return a
    if w == 1:
        return a.copy()
    kernel = np.full(w, 1.0 / w, dtype=float)
    return np.convolve(a, kernel, mode="valid")


def enhanced_filter_with_trend_preservation(x, window_size=20):
    """
    Exponential‑weighted moving average that emphasizes recent samples.
    The weight vector is reversed before convolution because ``np.convolve``
    flips the second argument.

    Returns an empty array if the input is shorter than ``window_size``.
    """
    a, w = _prepare(x, window_size)
    if a.size == 0:
        return a
    if w == 1:
        return a.copy()
    wts = _exp_weights(w)[::-1]
    return np.convolve(a, wts, mode="valid")


def median_filter(x, window_size=20):
    """
    Median filter that reduces impulsive noise while preserving edges.
    If ``window_size`` is even, it is increased by one to make it odd.

    Returns an empty array if the input is shorter than the (adjusted) window.
    """
    a, w = _prepare(x, window_size)
    if a.size == 0:
        return a
    # Ensure odd window size for a true median
    if w % 2 == 0:
        w += 1
        if a.size < w:
            return np.empty(0, dtype=float)
    # Use stride tricks for efficiency
    shape = (a.size - w + 1, w)
    strides = (a.strides[0], a.strides[0])
    windows = np.lib.stride_tricks.as_strided(a, shape=shape, strides=strides)
    return np.median(windows, axis=1)


def _auto_select(x):
    """
    Choose a filter based on signal variance.
    High variance → median (robust to outliers), otherwise enhanced.
    """
    arr = np.asarray(x, dtype=float).ravel()
    return median_filter if np.var(arr) > 1.0 else enhanced_filter_with_trend_preservation


def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """
    Dispatch to the selected filter.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "enhanced" : exponential‑weighted moving average (default)
        - "adaptive" : simple moving average
        - "median"   : median filter
        - "auto"     : automatic selection based on signal variance
    Unknown values fall back to the enhanced filter for robustness.
    """
    if algorithm_type is None:
        algorithm_type = "enhanced"
    key = str(algorithm_type).lower()

    if key == "adaptive":
        return adaptive_filter(input_signal, window_size)
    if key == "median":
        return median_filter(input_signal, window_size)
    if key == "auto":
        return _auto_select(input_signal)(input_signal, window_size)

    # Default / fallback
    return enhanced_filter_with_trend_preservation(input_signal, window_size)
