import numpy as np

def _to_numpy(x):
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()


def _validate_window(arr, w):
    """Return True if window size is a positive integer and fits the array."""
    return isinstance(w, int) and w > 0 and arr.size >= w


def _ma(arr, w):
    """Simple moving average (valid convolution)."""
    if not _validate_window(arr, w):
        return np.empty(0, float)
    return np.convolve(arr, np.ones(w) / w, mode="valid")


def _gaussian_weights(w):
    """Generate normalized Gaussian kernel of length w."""
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w, dtype=float)
    weights = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    return weights / weights.sum()


def _weighted(arr, w):
    """Gaussian‑weighted moving average."""
    if not _validate_window(arr, w):
        return np.empty(0, float)
    return np.convolve(arr, _gaussian_weights(w), mode="valid")


def _ema(arr, w):
    """Classic exponential moving average aligned with MA length."""
    if not _validate_window(arr, w):
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(arr)
    ema[0] = arr[0]
    for i in range(1, len(arr)):
        ema[i] = alpha * arr[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _median(arr, w):
    """Sliding‑window median filter (valid length)."""
    if not _validate_window(arr, w):
        return np.empty(0, float)
    stride = arr.strides[0]
    shape = (arr.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(arr, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _detrended_weighted(arr, w):
    """
    Detrend using a simple moving average, smooth the detrended series with a
    Gaussian kernel, then re‑add the trend.
    """
    if not _validate_window(arr, w):
        return np.empty(0, float)

    trend = _ma(arr, w)                     # length = n‑w+1
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = arr - trend_full
    smooth = _weighted(detrended, w)        # length = n‑w+1
    return smooth + trend                    # align with smooth output


def _residual_variance(orig, filt):
    """Mean‑squared deviation between overlapping portions of two signals."""
    if filt.size == 0:
        return float("inf")
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))


def _select_best(arr, w):
    """Choose the filter with the lowest residual variance (deterministic)."""
    candidates = {
        "ma": _ma(arr, w),
        "weighted": _weighted(arr, w),
        "ema": _ema(arr, w),
        "median": _median(arr, w),
        "detrended": _detrended_weighted(arr, w),
    }
    # Discard empty results
    filtered = [(name, data) for name, data in candidates.items() if data.size]
    if not filtered:
        return np.empty(0, float)

    # Compute residual variance and select minimal (tie‑break by name)
    best = min(
        ((_residual_variance(arr, data), name, data) for name, data in filtered),
        key=lambda t: (t[0], t[1]),
    )
    return best[2]


def process_signal(input_signal, window_size=20, algorithm_type="combined"):
    """
    Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "basic" / "ma"        : simple moving average
        - "weighted" / "gaussian": Gaussian‑weighted moving average
        - "ema" / "ewma"        : exponential moving average
        - "median"              : median filter
        - "detrended" / "trend": detrended‑weighted filter
        - "combined" / "enhanced": automatic best‑filter selection
        - any other value      : fallback to automatic selection
    """
    arr = _to_numpy(input_signal)
    w = int(window_size)

    key = (algorithm_type or "").lower()
    if key in ("basic", "ma"):
        return _ma(arr, w)
    if key in ("weighted", "gaussian"):
        return _weighted(arr, w)
    if key in ("ema", "ewma"):
        return _ema(arr, w)
    if key == "median":
        return _median(arr, w)
    if key in ("detrended", "trend"):
        return _detrended_weighted(arr, w)

    # For "combined", "enhanced", or any unknown type, select the best filter
    return _select_best(arr, w)
