import numpy as np
from functools import lru_cache
from typing import List, Callable, Dict

def _to_array(x) -> np.ndarray:
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr: np.ndarray, w: int) -> bool:
    """Check that window size is positive and fits the array."""
    return isinstance(w, int) and w > 0 and arr.size >= w

@lru_cache(maxsize=64)
def _exp_weights(w: int) -> np.ndarray:
    """Cached exponential weighting vector that sums to 1."""
    w = int(w)
    weights = np.exp(np.linspace(-2, 0, w))
    return weights / weights.sum()

def _apply_kernel(arr: np.ndarray, kernel: np.ndarray) -> np.ndarray:
    """Convolve array with kernel using 'valid' mode."""
    return np.convolve(arr, kernel, mode="valid")

def adaptive_filter(x, window_size: int = 20) -> np.ndarray:
    """Uniform moving‑average filter (valid output)."""
    a = _to_array(x)
    if not _valid(a, window_size):
        return np.empty(0, dtype=float)
    if window_size == 1:
        return a.copy()
    kernel = np.full(window_size, 1.0 / window_size, dtype=float)
    return _apply_kernel(a, kernel)

def weighted_filter(x, window_size: int = 20) -> np.ndarray:
    """Exponentially weighted moving‑average filter (valid output)."""
    a = _to_array(x)
    if not _valid(a, window_size):
        return np.empty(0, dtype=float)
    if window_size == 1:
        return a.copy()
    return _apply_kernel(a, _exp_weights(window_size))

def ema_filter(x, window_size: int = 20) -> np.ndarray:
    """Recursive exponential moving‑average (EMA) filter."""
    a = _to_array(x)
    if not _valid(a, window_size):
        return np.empty(0, dtype=float)
    if window_size == 1:
        return a.copy()
    alpha = 2.0 / (window_size + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[window_size - 1 :]

def hybrid_filter(x, window_size: int = 20) -> np.ndarray:
    """Average of adaptive and weighted filters (same length)."""
    a = adaptive_filter(x, window_size)
    w = weighted_filter(x, window_size)
    if a.size == 0:
        return w
    if w.size == 0:
        return a
    return (a + w) / 2.0

def _residual_variance(orig: np.ndarray, filt: np.ndarray) -> float:
    """Variance of the residual over the overlapping region."""
    n = min(len(orig), len(filt))
    return float("inf") if n == 0 else float(np.var(orig[:n] - filt[:n]))

def _auto_select(x, window_size: int = 20) -> np.ndarray:
    """Choose the filter with the lowest residual variance."""
    a = _to_array(x)
    candidates = {
        "adaptive": adaptive_filter(a, window_size),
        "weighted": weighted_filter(a, window_size),
        "ema": ema_filter(a, window_size),
        "hybrid": hybrid_filter(a, window_size),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
    return candidates[best_key]

def process_signal(input_signal, window_size: int = 20, algorithm_type: str = "enhanced") -> np.ndarray:
    """
    Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "adaptive" / "basic"
        - "weighted"
        - "ema"
        - "enhanced" / "hybrid"
        - "auto" (automatically selects the best filter)
        - any other value falls back to the hybrid filter.
    """
    alg = (algorithm_type or "enhanced").lower()
    mapping: Dict[str, Callable[[np.ndarray, int], np.ndarray]] = {
        "adaptive": adaptive_filter,
        "basic": adaptive_filter,
        "weighted": weighted_filter,
        "ema": ema_filter,
        "enhanced": hybrid_filter,
        "hybrid": hybrid_filter,
    }
    if alg == "auto":
        return _auto_select(input_signal, window_size)
    func = mapping.get(alg)
    if func:
        return func(input_signal, window_size)
    # Fallback to hybrid filter for unknown identifiers
    return hybrid_filter(input_signal, window_size)
