import numpy as np

def _to_numpy(arr):
    """Convert input to 1‑D float array."""
    return np.asarray(arr, dtype=float).ravel()


def _valid(arr, w):
    """Window size must be positive and fit the array."""
    return w > 0 and arr.size >= w


def _ma(x, w):
    """Simple moving average."""
    x = _to_numpy(x)
    if not _valid(x, w):
        return np.array([], float)
    return np.convolve(x, np.ones(w) / w, mode="valid")


def _ewma(x, w):
    """Exponential moving average aligned with MA length."""
    x = _to_numpy(x)
    if not _valid(x, w):
        return np.array([], float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(x)
    ema[0] = x[0]
    for i in range(1, len(x)):
        ema[i] = alpha * x[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _gaussian_weights(w):
    """Normalized Gaussian kernel."""
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w, dtype=float)
    wts = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    return wts / wts.sum()


def _gaussian(x, w):
    """Gaussian‑weighted moving average."""
    x = _to_numpy(x)
    if not _valid(x, w):
        return np.array([], float)
    return np.convolve(x, _gaussian_weights(w), mode="valid")


def _median(x, w):
    """Sliding‑window median filter."""
    x = _to_numpy(x)
    if not _valid(x, w):
        return np.array([], float)
    stride = x.strides[0]
    shape = (x.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(x, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _detrended_weighted(x, w):
    """
    Detrend with MA, smooth detrended part with Gaussian,
    then re‑add the trend.
    """
    x = _to_numpy(x)
    if not _valid(x, w):
        return np.array([], float)

    trend = _ma(x, w)                     # length n‑w+1
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = x - trend_full
    smooth = _gaussian(detrended, w)     # length n‑w+1
    return smooth + trend                # aligned output


def _combined(x, w):
    """Average of MA and EWMA where both exist."""
    ma = _ma(x, w)
    ew = _ewma(x, w)
    if ma.size and ew.size:
        return (ma + ew) / 2.0
    return ma if ma.size else ew


def _residual_var(orig, filt):
    """Mean‑squared deviation on overlapping region."""
    if filt.size == 0:
        return float("inf")
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n]))


def _best(x, w):
    """Select filter with lowest residual variance (deterministic tie‑break)."""
    x = _to_numpy(x)
    candidates = {
        "ma": _ma(x, w),
        "ewma": _ewma(x, w),
        "combined": _combined(x, w),
        "gaussian": _gaussian(x, w),
        "median": _median(x, w),
        "detrended": _detrended_weighted(x, w),
    }
    # Discard empty results
    viable = [(k, v) for k, v in candidates.items() if v.size]
    if not viable:
        return np.array([], float)

    # Compute residuals and pick smallest; alphabetical tie‑break
    best_key, best_arr = min(
        ((k, v) for k, v in viable),
        key=lambda kv: (_residual_var(x, kv[1]), kv[0]),
    )
    return best_arr


def process_signal(sig, w=20, algorithm_type="combined"):
    """
    Dispatch to a filtering algorithm.
    Supported types (case‑insensitive):
        basic/ma, ewma/ema, weighted, median, combined,
        detrended, enhanced – all fall back to auto‑selection.
    """
    key = (algorithm_type or "").lower()
    if key in ("basic", "ma"):
        return _ma(sig, w)
    if key in ("ewma", "ema"):
        return _ewma(sig, w)
    if key == "weighted":
        return _gaussian(sig, w)
    if key == "median":
        return _median(sig, w)
    if key == "combined":
        return _combined(sig, w)
    if key == "detrended":
        return _detrended_weighted(sig, w)
    # "enhanced" or any unknown value → best automatic filter
    return _best(sig, w)
