import numpy as np
from functools import lru_cache
from typing import List

def _prep(x, ws):
    ws = int(ws)
    if ws <= 0:
        raise ValueError(f"window_size must be positive, got {ws}")
    a = np.asarray(x, dtype=float).ravel()
    if a.size < ws:
        return np.empty(0, dtype=float), ws
    return a, ws

@lru_cache(maxsize=64)
def _expw(ws):
    w = np.exp(np.linspace(-2, 0, ws))
    return w / w.sum()

def adaptive_filter(x, window_size=20):
    a, ws = _prep(x, window_size)
    if a.size == 0:
        return a
    c = np.cumsum(np.insert(a, 0, 0.0))
    return (c[ws:] - c[:-ws]) / ws

def weighted_filter(x, window_size=20):
    a, ws = _prep(x, window_size)
    if a.size == 0:
        return a
    return np.convolve(a, _expw(ws), mode="valid")

def exponential_smoothing(x, window_size=20):
    a, ws = _prep(x, window_size)
    if a.size == 0:
        return a
    alpha = 2.0 / (ws + 1.0)
    y = np.empty_like(a)
    y[0] = a[0]
    for i in range(1, a.size):
        y[i] = alpha * a[i] + (1 - alpha) * y[i - 1]
    return y[ws - 1 :]

def _resvar(orig, filt):
    m = min(len(orig), len(filt))
    if m == 0:
        return float("inf")
    d = orig[:m] - filt[:m]
    return float(np.mean(d * d))

def _combined(x, ws):
    a, _ = _prep(x, ws)
    if a.size == 0:
        return a
    cand = {
        "basic": adaptive_filter(a, ws),
        "weighted": weighted_filter(a, ws),
        "exp": exponential_smoothing(a, ws),
    }
    vars = {k: _resvar(a, v) for k, v in cand.items() if v.size}
    if not vars:
        return np.empty(0, dtype=float)
    for k, v in vars.items():
        if v == 0.0:
            return cand[k][:len(a) - ws + 1]
    keys = sorted(vars)
    inv = np.array([1.0 / vars[k] for k in keys])
    wts = inv / inv.sum()
    min_len = min(len(cand[k]) for k in keys)
    combined = sum(wts[i] * cand[k][:min_len] for i, k in enumerate(keys))
    return combined

def _best(x, ws):
    return _combined(x, ws)

def process_signal(sig, window_size=20, algorithm_type="enhanced"):
    alg = str(algorithm_type).lower()
    if alg == "basic":
        return adaptive_filter(sig, window_size)
    if alg == "enhanced":
        return _combined(sig, window_size)
    if alg == "exp":
        return exponential_smoothing(sig, window_size)
    return _best(sig, window_size)

def construct_packing(items: List[int], capacity: int) -> List[List[int]]:
    """
    Best‑Fit Decreasing bin‑packing.

    Returns a list of bins, each bin being a list of original item indices.
    """
    if capacity <= 0:
        raise ValueError("capacity must be a positive integer")
    if not items:
        return []

    indexed = []
    for idx, size in enumerate(items):
        if not isinstance(size, (int, float)):
            raise TypeError("item sizes must be numeric")
        if size < 0:
            raise ValueError("item sizes must be non‑negative")
        indexed.append((idx, float(size)))

    indexed.sort(key=lambda x: x[1], reverse=True)

    bins: List[List[int]] = []
    remaining: List[float] = []

    for idx, size in indexed:
        if size > capacity:
            bins.append([idx])
            remaining.append(0.0)
            continue

        best_bin = -1
        best_rem = float("inf")
        for b_idx, cap_left in enumerate(remaining):
            if size <= cap_left and cap_left - size < best_rem:
                best_bin = b_idx
                best_rem = cap_left - size

        if best_bin >= 0:
            bins[best_bin].append(idx)
            remaining[best_bin] -= size
        else:
            bins.append([idx])
            remaining.append(capacity - size)

    for b in bins:
        b.sort()
    return bins
