    return float(np.mean((orig[:n] - filt[:n]) ** 2))

def _select_best(a: np.ndarray, w: int) -> np.ndarray:
    """Choose the filter with the lowest residual variance."""
    candidates: Dict[str, Callable[[np.ndarray, int], np.ndarray]] = {
        "adaptive": adaptive_filter,
        "weighted": weighted_filter,
        "median": median_filter,
        "ema": ema_filter,
        "hybrid": hybrid_filter,
    }
    best_key = min(
        candidates,
        key=lambda k: _residual_variance(a, candidates[k](a, w))
    )
    return candidates[best_key](a, w)

def process_signal(sig, w: int = 20, alg: str = "auto"):
    """
    Apply a filter to ``sig``.

    Parameters
    ----------
    sig : array‑like
        Input signal.
    w : int, optional
        Window size (default 20). Must be positive and ≤ len(sig).
    alg : str, optional
        Algorithm selector (case‑insensitive, prefix matching):
        - "adaptive" (or "a") → simple moving average
        - "median"   (or "m") → median filter
        - "weighted"/"enhanced" (or "e") → exponential‑weighted MA
        - "ema"      (or "em") → recursive EMA
        - "hybrid"   (or "h") → average of adaptive and weighted
        - "auto"     (or any other) → choose best based on residual variance

    Returns
    -------
    np.ndarray
        Filtered signal.
    """
    a = _to_array(sig)
    _validate_window(a, w)

    key = alg.lower()
    if key.startswith("a"):
        return adaptive_filter(a, w)
    if key.startswith("m"):
        return median_filter(a, w)
    if key.startswith("e"):
        return weighted_filter(a, w)
    if key.startswith("em"):
        return ema_filter(a, w)
    if key.startswith("h"):
        return hybrid_filter(a, w)
    if key.startswith("auto"):
        return _select_best(a, w)

    # Fallback to the automatically selected best filter
    return _select_best(a, w)
