    if a.size == 0:
        return a
    if w == 1:
        return a.copy()
    # Trend via simple moving average
    trend = adaptive_filter(a, w)
    if trend.size == 0:
        # Fallback to weighted filter if trend cannot be computed
        return weighted_filter(a, w)
    # Pad trend to original length (repeat first value for the leading window)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = a - trend_full
    # Smooth detrended signal
    smoothed = weighted_filter(detrended, w)
    # Align trend segment with smoothed output
    trend_aligned = trend_full[w - 1 :]
