import numpy as np


def _to_numpy(x):
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()


def _validate_window(w):
    """Ensure window size is a positive integer."""
    if not isinstance(w, int) or w <= 0:
        raise ValueError("window_size must be a positive integer")


def _ma(x, w):
    """Simple moving average."""
    _validate_window(w)
    a = _to_numpy(x)
    if a.size < w:
        return np.array([], float)
    return np.convolve(a, np.ones(w) / w, mode="valid")


def _gaussian(x, w):
    """Gaussian‑weighted moving average."""
    _validate_window(w)
    a = _to_numpy(x)
    if a.size < w:
        return np.array([], float)
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w)
    wts = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    wts /= wts.sum()
    return np.convolve(a, wts, mode="valid")


def _ema(x, w):
    """Exponential moving average aligned to other filters."""
    _validate_window(w)
    a = _to_numpy(x)
    if a.size < w:
        return np.array([], float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _median(x, w):
    """Median filter."""
    _validate_window(w)
    a = _to_numpy(x)
    if a.size < w:
        return np.array([], float)
    stride = a.strides[0]
    shape = (a.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(a, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _linear_detrend(arr):
    """Remove linear trend via least‑squares."""
    n = arr.size
    if n == 0:
        return arr
    t = np.arange(n, dtype=float)
    A = np.vstack([t, np.ones_like(t)]).T
    slope, intercept = np.linalg.lstsq(A, arr, rcond=None)[0]
    return arr - (slope * t + intercept)


def _combined(x, w):
    """Detrend → Gaussian → median."""
    _validate_window(w)
    a = _to_numpy(x)
    if a.size < w:
        return np.array([], float)
    detrended = _linear_detrend(a)
    filtered = _gaussian(detrended, w)
    if filtered.size == 0:
        return np.array([], float)
    return _median(filtered, w)


def _residual_variance(orig, filt):
    """Variance of (orig - filt) over overlapping region."""
    if filt.size == 0:
        return float("inf")
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n]))


def _best_filter(x, w):
    """Select filter with lowest residual variance (deterministic tie‑break)."""
    a = _to_numpy(x)
    if a.size < w:
        return np.array([], float)

    candidates = {
        "combined": _combined(a, w),
        "gaussian": _gaussian(a, w),
        "ema": _ema(a, w),
        "median": _median(a, w),
        "ma": _ma(a, w),
    }

    # Keep only non‑empty results
    viable = {k: v for k, v in candidates.items() if v.size}
    if not viable:
        return np.array([], float)

    variances = {k: _residual_variance(a, v) for k, v in viable.items()}
    # Deterministic order for ties
    order = ("combined", "gaussian", "ema", "median", "ma")
    best_key = min(order, key=lambda k: (variances.get(k, float("inf")), k))
    return viable[best_key]


def process_signal(sig, w=20, algorithm_type="combined", **kwargs):
    """
    Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` (case‑insensitive):
        basic/ma, weighted, ema, median, enhanced/combined/auto.
    """
    _validate_window(w)
    a = _to_numpy(sig)
    if a.size == 0:
        return np.array([], float)

    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        result = _ma(a, w)
    elif alg == "weighted":
        result = _gaussian(a, w)
    elif alg in ("ema", "ewma"):
        result = _ema(a, w)
    elif alg == "median":
        result = _median(a, w)
    else:
        result = _best_filter(a, w)

    if kwargs.get("debug"):
        try:
            result.debug_info = {"debug": True}
        except Exception:
            pass

    return result
