import numpy as np
from functools import lru_cache

def _arr(x): return np.asarray(x, dtype=float).ravel()
def _ma(x,w): a=_arr(x); return np.convolve(a,np.ones(w)/w,"valid") if w>0 and a.size>=w else np.empty(0)
def _ema(x,w):
    a=_arr(x)
    if w<=0 or a.size<w: return np.empty(0)
    a0=a[0]; out=np.empty_like(a); out[0]=a0
    α=2.0/(w+1)
    for i in range(1,len(a)): out[i]=α*a[i]+(1-α)*out[i-1]
    return out[w-1:]

@lru_cache(maxsize=None)
def _gw(w):
    σ=0.3*w; c=(w-1)/2.0; idx=np.arange(w)
    wts=np.exp(-0.5*((idx-c)/σ)**2); return wts/wts.sum()
def _gauss(x,w): a=_arr(x); return np.convolve(a,_gw(w),"valid") if w>0 and a.size>=w else np.empty(0)

def _median(x,w):
    a=_arr(x)
    if w<=0 or a.size<w: return np.empty(0)
    s=a.strides[0]; shape=(a.size-w+1,w); win=np.lib.stride_tricks.as_strided(a,shape=shape,strides=(s,s))
    return np.median(win,axis=1)

def _resvar(o,f):
    if f.size==0: return np.inf
    n=min(len(o),len(f)); return np.var(o[:n]-f[:n])

def _best(x,w):
    a=_arr(x)
    cand={"ma":_ma(a,w),"ema":_ema(a,w),"gauss":_gauss(a,w),"median":_median(a,w)}
    cand={k:v for k,v in cand.items() if v.size}
    return min(cand.values(),key=lambda f:_resvar(a,f)) if cand else np.empty(0)

def adaptive_filter(x,w=20): return _ma(x,w)

def process_signal(sig,w=20,algorithm_type="combined"):
    a=algorithm_type.lower()
    if a in ("basic","ma"): return _ma(sig,w)
    if a in ("ewma","ema"): return _ema(sig,w)
    if a in ("weighted","gauss","gaussian"): return _gauss(sig,w)
    if a=="median": return _median(sig,w)
    return _best(sig,w)

def construct_packing(items,capacity):
    if not isinstance(capacity,int) or capacity<=0: raise ValueError("capacity must be a positive integer")
    if not isinstance(items,(list,tuple)): raise ValueError("items must be a list or tuple")
    for i,s in enumerate(items):
        if not isinstance(s,(int,float)): raise ValueError(f"Item at index {i} is not a number")
        if s<0: raise ValueError(f"Item size must be non‑negative, got {s}")
    if not items: return []
    indexed=sorted(((i,s) for i,s in enumerate(items)),key=lambda x:x[1],reverse=True)
    bins=[]; rem=[]
    for i,s in indexed:
        if s>capacity:
            bins.append([i]); rem.append(0); continue
        for b,space in enumerate(rem):
            if s<=space:
                bins[b].append(i); rem[b]-=s; break
        else:
            bins.append([i]); rem.append(capacity-s)
    for b in bins: b.sort()
    return bins
